import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useProducts } from '../../contexts/ProductContext'; // ‚Üê Import do context
import './ProductPage.css';

const ProductPage = () => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const { products } = useProducts(); // ‚Üê Use o context
  
  const product = products.find(p => p.id === parseInt(productId));

  // Se produto n√£o for encontrado
  if (!product) {
    return (
      <div className="product-not-found">
        <div className="container">
          <h2>Produto n√£o encontrado</h2>
          <p>O produto que voc√™ est√° procurando n√£o existe.</p>
          <button onClick={() => navigate('/cardapio')} className="back-button">
            Voltar ao Card√°pio
          </button>
        </div>
      </div>
    );
  }

  const handleBackClick = () => {
    navigate(-1); // Volta para a p√°gina anterior
  };

  const handleAddToCart = () => {
    // Aqui voc√™ pode implementar a l√≥gica do carrinho
    alert(`${product.name} adicionado ao carrinho!`);
  };

  return (
    <div className="product-page">
      <div className="container">
        {/* Bot√£o Voltar */}
        <button onClick={handleBackClick} className="product-back-button">
          ‚Üê Voltar
        </button>

        <div className="product-layout">
          {/* Imagem do Produto */}
          <div className="product-image-section">
            <div className="product-image-container">
              {product.image ? (
                <img 
                  src={product.image} 
                  alt={product.name}
                  className="product-image"
                  onError={(e) => {
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'flex';
                  }}
                />
              ) : null}
              <div className="product-image-placeholder">
                <span>üçΩÔ∏è</span>
                <p>Imagem n√£o dispon√≠vel</p>
              </div>
            </div>
          </div>

          {/* Informa√ß√µes do Produto */}
          <div className="product-info-section">
            <h1 className="product-title">{product.name}</h1>
            
            {product.description && (
              <p className="product-description">{product.description}</p>
            )}

            {/* Se√ß√£o de Pre√ßos */}
            {product.details ? (
              <div className="price-section">
                <h3>Op√ß√µes de Pre√ßo</h3>
                <div className="price-options">
                  {product.details.individual && product.details.individual !== "N√£o dispon√≠vel" && (
                    <div className="price-option">
                      <span className="price-label">Individual</span>
                      <span className="price-value">{product.details.individual}</span>
                    </div>
                  )}
                  {product.details.media && product.details.media !== "N√£o dispon√≠vel" && (
                    <div className="price-option">
                      <span className="price-label">M√©dia</span>
                      <span className="price-value">{product.details.media}</span>
                    </div>
                  )}
                  {product.details.grande && product.details.grande !== "N√£o dispon√≠vel" && (
                    <div className="price-option">
                      <span className="price-label">Grande</span>
                      <span className="price-value">{product.details.grande}</span>
                    </div>
                  )}
                </div>
              </div>
            ) : (
              <div className="price-single-section">
                <h3>Pre√ßo</h3>
                <div className="single-price">
                  <span className="price-value">R$ {product.price.toFixed(2)}</span>
                </div>
              </div>
            )}

            {/* Observa√ß√µes */}
            {(product.details?.observacao || product.observacao) && (
              <div className="product-observation">
                <strong>Observa√ß√£o:</strong> {product.details?.observacao || product.observacao}
              </div>
            )}

            {/* Bot√£o de Adicionar ao Carrinho */}
            <button onClick={handleAddToCart} className="add-to-cart-button">
              Adicionar ao Carrinho
            </button>

            {/* Informa√ß√µes Adicionais */}
            <div className="product-meta">
              <div className="meta-item">
                <strong>Categoria:</strong> 
                <span>{getCategoryName(product.category)}</span>
              </div>
              {product.subcategory && (
                <div className="meta-item">
                  <strong>Subcategoria:</strong> 
                  <span>{getSubcategoryName(product.subcategory)}</span>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Fun√ß√µes auxiliares para converter IDs em nomes
const getCategoryName = (categoryId) => {
  const categories = {
    'todos': 'Todos',
    'happy-hour': 'Happy Hour',
    'almoco': 'Almo√ßo',
    'jantar': 'Jantar',
    'espetos': 'Espetos',
    'porcoes': 'Por√ß√µes',
    'cervejas': 'Cervejas',
    'bebidas': 'Bebidas',
    'drinks': 'Drinks',
    'aperitivo': 'Aperitivo'
  };
  return categories[categoryId] || categoryId;
};

const getSubcategoryName = (subcategoryId) => {
  const subcategories = {
    'todos': 'Todos',
    'porcoes-happy': 'Por√ß√µes Happy',
    'cervejas-happy': 'Cervejas Happy',
    'caipirinhas-happy': 'Caipirinhas Happy',
    'comida-boteco': 'Comida de Boteco',
    'porcoes-boteco': 'Por√ß√µes de Boteco',
    'bolinhos-boteco': 'Bolinhos de Boteco',
    'guarnicoes': 'Guarni√ß√µes',
    'drinks': 'Drinks',
    'caipirinhas': 'Caipirinhas',
    'doses': 'Doses',
    'sucos': 'Sucos',
    'refrigerantes': 'Refrigerantes',
    '600ml': '600ml',
    'long-neck': 'Long Neck',
    'almoco': 'Almo√ßo'
  };
  return subcategories[subcategoryId] || subcategoryId;
};

export default ProductPage;
