    },
    {
      id: 158,
      name: "Imp√©rio Lager",
      price: 10.90,
      category: CATEGORIES.HAPPY_HOUR,
      subcategory: SUBCATEGORIES.CERVEJAS_HAPPY
    },
    {
      id: 159,
      name: "Petra",
      price: 9.90,
      category: CATEGORIES.HAPPY_HOUR,
      subcategory: SUBCATEGORIES.CERVEJAS_HAPPY
    },
    {
      id: 160,
      name: "Skol",
      price: 9.90,
      category: CATEGORIES.HAPPY_HOUR,
      subcategory: SUBCATEGORIES.CERVEJAS_HAPPY
    },
    {
      id: 161,
      name: "Smith",
      price: 10.90,
      category: CATEGORIES.HAPPY_HOUR,
      subcategory: SUBCATEGORIES.CERVEJAS_HAPPY
    },

    // HAPPY HOUR - CAIPIRINHAS
    {
      id: 162,
      name: "Caipirinha Vodka Happy",
      description: "Lim√£o ou Morango",
      price: 13.90,
      category: CATEGORIES.HAPPY_HOUR,
      subcategory: SUBCATEGORIES.CAIPIRINHAS_HAPPY
    },
    {
      id: 163,
      name: "Caipirinha Cacha√ßa Happy",
      description: "Lim√£o ou Morango",
      price: 13.90,
      category: CATEGORIES.HAPPY_HOUR,
      subcategory: SUBCATEGORIES.CAIPIRINHAS_HAPPY
    }
  ];

  // L√≥gica de filtragem
  const filteredProducts = products.filter(product => {
    const matchesCategory = activeCategory === CATEGORIES.TODOS || product.category === activeCategory;
    const matchesSubcategory = activeSubcategory === SUBCATEGORIES.TODOS || product.subcategory === activeSubcategory;
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
                       (product.description && product.description.toLowerCase().includes(searchTerm.toLowerCase()));
    
    if (subcategories[activeCategory]) {
      return matchesCategory && matchesSubcategory && matchesSearch;
    }
    return matchesCategory && matchesSearch;
  });

  // Handlers
  const handleProductClick = (product) => {
    setSelectedProduct(product);
  };

  const closeModal = () => {
    setSelectedProduct(null);
  };

  const handleCategoryChange = (categoryId) => {
    setActiveCategory(categoryId);
    setActiveSubcategory(SUBCATEGORIES.TODOS);
  };

  // Fun√ß√£o para fallback de emojis caso as imagens n√£o carreguem
  const getFallbackEmoji = (categoryId) => {
    const emojiMap = {
      [CATEGORIES.TODOS]: 'üçΩÔ∏è',
      [CATEGORIES.HAPPY_HOUR]: 'üçπ',
      [CATEGORIES.ALMOCO]: 'üåû',
      [CATEGORIES.JANTAR]: 'üåô',
      [CATEGORIES.ESPETOS]: 'üç¢',
      [CATEGORIES.PORCOES]: 'üçü',
      [CATEGORIES.CERVEJAS]: 'üç∫',
      [CATEGORIES.BEBIDAS]: 'ü•§',
      [CATEGORIES.DRINKS]: 'üç∏',
      [CATEGORIES.APERITIVO]: 'ü•Ç'
    };
    return emojiMap[categoryId] || 'üìÅ';
  };

  // Componente de Modal
  const ProductModal = () => (
    <div className="modal-overlay" onClick={closeModal}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="modal-close" onClick={closeModal}>√ó</button>
        <h2>{selectedProduct.name}</h2>
        <p className="modal-description">{selectedProduct.description}</p>
        
        {selectedProduct.details ? (
          <div className="price-details">
            <h3>Valores:</h3>
            <div className="price-options">
              {selectedProduct.category === CATEGORIES.PORCOES ? (
                <>
                  {selectedProduct.details.individual !== "N√£o dispon√≠vel" && (
                    <div className="price-option">
                      <span className="price-label">Meia por√ß√£o:</span>
                      <span className="price-value">{selectedProduct.details.individual}</span>
                    </div>
                  )}
                  {selectedProduct.details.media !== "N√£o dispon√≠vel" && (
                    <div className="price-option">
                      <span className="price-label">Inteira:</span>
                      <span className="price-value">{selectedProduct.details.media}</span>
                    </div>
                  )}
                  {selectedProduct.details.grande !== "N√£o dispon√≠vel" && (
                    <div className="price-option">
                      <span className="price-label">Fam√≠lia:</span>
                      <span className="price-value">{selectedProduct.details.grande}</span>
                    </div>
                  )}
                </>
              ) : (
                <>
                  {selectedProduct.details.individual !== "N√£o dispon√≠vel" && (
                    <div className="price-option">
                      <span className="price-label">Executivo (1 pessoa):</span>
                      <span className="price-value">{selectedProduct.details.individual}</span>
                    </div>
                  )}
                  {selectedProduct.details.media !== "N√£o dispon√≠vel" && (
                    <div className="price-option">
                      <span className="price-label">Comercial (2 pessoas):</span>
                      <span className="price-value">{selectedProduct.details.media}</span>
                    </div>
                  )}
                  {selectedProduct.details.grande !== "N√£o dispon√≠vel" && (
                    <div className="price-option">
                      <span className="price-label">√Ä La Carte (at√© 4 pessoas):</span>
                      <span className="price-value">{selectedProduct.details.grande}</span>
                    </div>
                  )}
                </>
              )}
            </div>
            {selectedProduct.details.observacao && (
              <p className="modal-observation">{selectedProduct.details.observacao}</p>
            )}
          </div>
        ) : (
          <div className="price-single">
            <span className="modal-price">R$ {formatPrice(selectedProduct.price)}</span>
            {selectedProduct.observacao && (
              <p className="modal-observation">{selectedProduct.observacao}</p>
            )}
          </div>
        )}
      </div>
    </div>
  );

  return (
    <div className="menu-page">
      <div className="container">
        <h1>Card√°pio</h1>
        
        <div className="menu-controls">
          <div className="search-bar">
            <input
              type="text"
              placeholder="Buscar produto..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          
          <div className="category-filters">
            {categories.map(category => (
              <button
                key={category.id}
                className={`category-btn ${activeCategory === category.id ? 'active' : ''}`}
                onClick={() => handleCategoryChange(category.id)}
              >
                <img 
                  src={category.image} 
                  alt={category.name}
                  className="category-image"
                  onError={(e) => {
                    // Fallback caso a imagem n√£o carregue
                    e.target.style.display = 'none';
                    const fallbackSpan = e.target.nextSibling;
                    if (fallbackSpan) {
                      fallbackSpan.style.display = 'inline';
                    }
                  }}
                />
                {/* Fallback em texto caso a imagem n√£o carregue */}
                <span style={{display: 'none', fontSize: '1.2rem'}}>
                  {getFallbackEmoji(category.id)}
                </span>
                <span>{category.name}</span>
              </button>
            ))}
          </div>

          {/* Filtros de Subcategoria */}
          {subcategories[activeCategory] && (
            <div className="subcategory-filters">
              {subcategories[activeCategory].map(subcategory => (
                <button
                  key={subcategory.id}
                  className={`subcategory-btn ${activeSubcategory === subcategory.id ? 'active' : ''}`}
                  onClick={() => setActiveSubcategory(subcategory.id)}
                >
                  {subcategory.name}
                </button>
              ))}
            </div>
          )}
        </div>
        
        <div className="products-grid">
          {filteredProducts.map(product => (
            <ProductCard 
              key={product.id} 
              product={product} 
              onProductClick={handleProductClick}
            />
          ))}
        </div>
        
        {filteredProducts.length === 0 && (
          <div className="no-results">
            <p>Nenhum produto encontrado para sua busca.</p>
          </div>
        )}
      </div>

      {/* Modal de Detalhes do Produto */}
      {selectedProduct && <ProductModal />}
    </div>
  );
};

export default Menu;
