import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ProductPage.css';

// Esta função precisa receber os produtos como prop ou buscar de outra forma
const ProductPage = () => {
  const { productId } = useParams();
  const navigate = useNavigate();
  
  // Por enquanto, vamos usar um array vazio
  // Você precisará passar os produtos do Menu para cá
  const products = [];
  
  const product = products.find(p => p.id === parseInt(productId));

  if (!product) {
    return (
      <div className="product-not-found">
        <div className="container">
          <h2>Produto não encontrado</h2>
          <p>O produto que você está procurando não existe.</p>
          <button onClick={() => navigate('/cardapio')} className="back-button">
            Voltar ao Cardápio
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="product-page">
      <div className="container">
        <button onClick={() => navigate(-1)} className="product-back-button">
          ← Voltar
        </button>
        
        <div className="product-layout">
          <div className="product-image-section">
            {/* Conteúdo da página do produto */}
          </div>
          <div className="product-info-section">
            <h1>{product.name}</h1>
            {/* Mais conteúdo aqui */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
