import React, { useState } from 'react';
import './Review.css';

const Review = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    rating: 0,
    comment: '',
    consent: false
  });
  const [hoverRating, setHoverRating] = useState(0);
  const [contactMethod, setContactMethod] = useState('email');

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleRatingClick = (stars) => {
    setFormData({
      ...formData,
      rating: stars
    });
  };

const handleSubmit = async (e) => {
  e.preventDefault();

  const payload = {
    name: formData.name,
    email: formData.email,
    phone: formData.phone,
    rating: formData.rating,
    comment: formData.comment,
    consent: formData.consent,
    contactMethod: contactMethod
  };

  try {
    // quando estiver no Netlify, esse caminho funciona
    const response = await fetch('/.netlify/functions/save-review', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload),
    });

    if (!response.ok) {
      const err = await response.json().catch(() => ({}));
      throw new Error(err.error || 'Erro ao enviar avaliaÃ§Ã£o');
    }

    alert('Obrigado pela sua avaliaÃ§Ã£o!');

    // limpa o form
    setFormData({
      name: '',
      email: '',
      phone: '',
      rating: 0,
      comment: '',
      consent: false
    });
    setHoverRating(0);
    setContactMethod('email');
  } catch (error) {
    console.error(error);
    alert('NÃ£o foi possÃ­vel enviar agora. Tente novamente.');
  }
};

  // Componente de Estrelas Interativas
  const StarRating = () => {
    return (
      <div className="star-rating">
        {[1, 2, 3, 4, 5].map(star => (
          <button
            key={star}
            type="button"
            className={`star ${star <= (hoverRating || formData.rating) ? 'filled' : ''}`}
            onClick={() => handleRatingClick(star)}
            onMouseEnter={() => setHoverRating(star)}
            onMouseLeave={() => setHoverRating(0)}
            aria-label={`Avaliar com ${star} estrela${star > 1 ? 's' : ''}`}
          >
            â˜…
          </button>
        ))}
        <span className="rating-text">
          {formData.rating > 0 ? `${formData.rating} estrela${formData.rating > 1 ? 's' : ''}` : 'Clique para avaliar'}
        </span>
      </div>
    );
  };

  return (
    <div className="review-page">
      <div className="container">
        <h1>Avalie sua ExperiÃªncia</h1>
        
        <div className="review-content">
          <div className="review-form">
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="name">Nome Completo:</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                  placeholder="Seu nome completo"
                />
              </div>
              
             <div className="form-group">
  <label>Como prefere ser contatado?</label>
  <div className="contact-method">
    <div className="radio-option">
      <input
        type="radio"
        id="email-contact"
        name="contactMethod"
        value="email"
        checked={contactMethod === 'email'}
        onChange={(e) => setContactMethod(e.target.value)}
      />
      <label htmlFor="email-contact" className="radio-label">
        <span>ðŸ“§ Email</span>
      </label>
    </div>
    <div className="radio-option">
      <input
        type="radio"
        id="phone-contact"
        name="contactMethod"
        value="phone"
        checked={contactMethod === 'phone'}
        onChange={(e) => setContactMethod(e.target.value)}
      />
      <label htmlFor="phone-contact" className="radio-label">
        <span>ðŸ“ž Telefone</span>
      </label>
    </div>
  </div>
</div>
              
              <div className="form-group">
                <label htmlFor={contactMethod}>
                  {contactMethod === 'email' ? 'Email:' : 'Telefone:'}
                </label>
                <input
                  type={contactMethod === 'email' ? 'email' : 'tel'}
                  id={contactMethod}
                  name={contactMethod}
                  value={formData[contactMethod]}
                  onChange={handleChange}
                  required
                  placeholder={
                    contactMethod === 'email' 
                      ? 'seu@email.com' 
                      : '(00) 00000-0000'
                  }
                />
              </div>
              
              <div className="form-group">
                <label>AvaliaÃ§Ã£o:</label>
                <StarRating />
              </div>
              
              <div className="form-group">
                <label htmlFor="comment">ComentÃ¡rio:</label>
                <textarea
                  id="comment"
                  name="comment"
                  value={formData.comment}
                  onChange={handleChange}
                  rows="5"
                  required
                  placeholder="Conte-nos sobre sua experiÃªncia..."
                ></textarea>
              </div>

              <div className="form-group consent-group">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    name="consent"
                    checked={formData.consent}
                    onChange={handleChange}
                    required
                  />
                  <span>Concordo em compartilhar minha avaliaÃ§Ã£o publicamente</span>
                </label>
              </div>
              
              <button type="submit" className="btn-submit">
                 Enviar AvaliaÃ§Ã£o
              </button>
            </form>
          </div>
          
          <div className="reviews-list">
            <h2>AvaliaÃ§Ãµes Recentes</h2>
            <div className="review-item">
              <div className="review-header">
                <h3>Maria Silva</h3>
                <div className="rating">â˜…â˜…â˜…â˜…â˜…</div>
              </div>
              <p>"Adorei os espetos! Ambiente muito agradÃ¡vel e atendimento excelente."</p>
            </div>
            
            <div className="review-item">
              <div className="review-header">
                <h3>JoÃ£o Santos</h3>
                <div className="rating">â˜…â˜…â˜…â˜…â˜†</div>
              </div>
              <p>"A parmegiana simplesmente estava deliciosa! Voltarei com certeza."</p>
            </div>

            <div className="review-item">
              <div className="review-header">
                <h3>Ana Costa</h3>
                <div className="rating">â˜…â˜…â˜…â˜…â˜…</div>
              </div>
              <p>"Happy hour incrÃ­vel! Drinks bem feitos e petiscos de qualidade."</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Review;
