import React, { useState } from 'react';
import './Review.css';

const Review = () => {
  const [rating, setRating] = useState(0);
  const [hoverRating, setHoverRating] = useState(0);
  const [contactMethod, setContactMethod] = useState('email');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    review: '',
    consent: false
  });

  const handleRatingClick = (stars) => {
    setRating(stars);
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aqui vocÃª pode adicionar a lÃ³gica para enviar a avaliaÃ§Ã£o
    console.log({
      ...formData,
      rating,
      contactMethod
    });
    alert('AvaliaÃ§Ã£o enviada com sucesso! Obrigado pelo feedback.');
    
    // Reset do formulÃ¡rio
    setRating(0);
    setFormData({
      name: '',
      email: '',
      phone: '',
      review: '',
      consent: false
    });
  };

  // Componente de Estrelas
  const StarRating = () => {
    return (
      <div className="star-rating">
        {[1, 2, 3, 4, 5].map((star) => (
          <button
            key={star}
            type="button"
            className={`star ${star <= (hoverRating || rating) ? 'filled' : ''}`}
            onClick={() => handleRatingClick(star)}
            onMouseEnter={() => setHoverRating(star)}
            onMouseLeave={() => setHoverRating(0)}
            aria-label={`Avaliar com ${star} estrela${star > 1 ? 's' : ''}`}
          >
            â˜…
          </button>
        ))}
        <span className="rating-text">
          {rating > 0 ? `${rating} estrela${rating > 1 ? 's' : ''}` : 'Clique para avaliar'}
        </span>
      </div>
    );
  };

  return (
    <div className="review-page">
      <div className="container">
        <h1>Avalie sua ExperiÃªncia</h1>
        
        <div className="review-content">
          <div className="review-form-section">
            <form onSubmit={handleSubmit} className="review-form">
              <div className="form-group">
                <label htmlFor="name">Nome Completo *</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                  placeholder="Seu nome completo"
                />
              </div>

              <div className="form-group">
                <label>Como prefere ser contactado?</label>
                <div className="contact-method">
                  <label className="radio-label">
                    <input
                      type="radio"
                      name="contactMethod"
                      value="email"
                      checked={contactMethod === 'email'}
                      onChange={(e) => setContactMethod(e.target.value)}
                    />
                    <span>ðŸ“§ Email</span>
                  </label>
                  <label className="radio-label">
                    <input
                      type="radio"
                      name="contactMethod"
                      value="phone"
                      checked={contactMethod === 'phone'}
                      onChange={(e) => setContactMethod(e.target.value)}
                    />
                    <span>ðŸ“ž Telefone</span>
                  </label>
                </div>
              </div>

              <div className="form-group">
                <label htmlFor={contactMethod}>
                  {contactMethod === 'email' ? 'Email *' : 'Telefone *'}
                </label>
                <input
                  type={contactMethod === 'email' ? 'email' : 'tel'}
                  id={contactMethod}
                  name={contactMethod}
                  value={formData[contactMethod]}
                  onChange={handleInputChange}
                  required
                  placeholder={
                    contactMethod === 'email' 
                      ? 'seu@email.com' 
                      : '(00) 00000-0000'
                  }
                />
              </div>

              <div className="form-group">
                <label>AvaliaÃ§Ã£o *</label>
                <StarRating />
              </div>

              <div className="form-group">
                <label htmlFor="review">ComentÃ¡rio (opcional)</label>
                <textarea
                  id="review"
                  name="review"
                  value={formData.review}
                  onChange={handleInputChange}
                  placeholder="Conte-nos sobre sua experiÃªncia..."
                  rows="4"
                />
              </div>

              <div className="form-group consent-group">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    name="consent"
                    checked={formData.consent}
                    onChange={handleInputChange}
                    required
                  />
                  <span>Concordo em compartilhar minha avaliaÃ§Ã£o publicamente *</span>
                </label>
              </div>

              <button type="submit" className="submit-btn">
                ðŸ“¨ Enviar AvaliaÃ§Ã£o
              </button>
            </form>
          </div>

          <div className="reviews-section">
            <h2>AvaliaÃ§Ãµes Recentes</h2>
            
            <div className="review-card">
              <div className="review-header">
                <h3>Maria Silva</h3>
                <div className="review-stars">
                  {'â˜…'.repeat(5)}
                  <span className="star-count">5.0</span>
                </div>
              </div>
              <p className="review-text">"Adorei os espetos! Ambiente muito agradÃ¡vel e atendimento excelente."</p>
              <span className="review-date">HÃ¡ 2 dias</span>
            </div>

            <div className="review-card">
              <div className="review-header">
                <h3>JoÃ£o Santos</h3>
                <div className="review-stars">
                  {'â˜…'.repeat(4)}
                  <span className="star-count">4.0</span>
                </div>
              </div>
              <p className="review-text">"A parmegiana simplesmente estava deliciosa! Voltarei com certeza."</p>
              <span className="review-date">HÃ¡ 1 semana</span>
            </div>

            <div className="review-card">
              <div className="review-header">
                <h3>Ana Costa</h3>
                <div className="review-stars">
                  {'â˜…'.repeat(5)}
                  <span className="star-count">5.0</span>
                </div>
              </div>
              <p className="review-text">"Happy hour incrÃ­vel! Drinks bem feitos e petiscos de qualidade."</p>
              <span className="review-date">HÃ¡ 3 dias</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Review;
