import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import WhatsAppButton from './components/WhatsAppButton';
import FloatingIfood from './components/FloatingIfood'; // ← Adicione esta importação
import Home from './pages/Home';
import Menu from './pages/Menu';
import About from './pages/About';
import Review from './pages/Review';
import Ambiente from './pages/Ambiente'; 
import ProductPage from './components/ProductPage';
import './styles/globals.css';

function App() {
  return (
    <Router>
      <div className="App">
        <Header />
        <main>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/cardapio" element={<Menu />} />
            <Route path="/produto/:productId" element={<ProductPage products={products} />} />
            <Route path="/" element={<Home />} />
            <Route path="/cardapio" element={<Menu />} />
            <Route path="/sobre" element={<About />} />
            <Route path="/avalie" element={<Review />} />
            <Route path="/ambiente" element={<Ambiente />} />
          </Routes>
        </main>
        <WhatsAppButton />
        <FloatingIfood /> {/* ← Adicione esta linha */}
        <Footer />
      </div>
    </Router>
  );
}

export default App;
