import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const location = useLocation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  // Detecta scroll s√≥ no mobile, mant√©m classe .scrolled
  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.pageYOffset;
      if (scrollTop > 50) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    const isMobile = window.innerWidth <= 768;
    if (isMobile) {
      window.addEventListener('scroll', handleScroll);
    }

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Fecha menu ao trocar de rota
  useEffect(() => {
    setIsMenuOpen(false);
  }, [location]);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <header className={`header ${isScrolled ? 'scrolled' : ''}`}>
      <div className="container">
        {/* Logo / t√≠tulo */}
        <Link to="/" className="logo" onClick={closeMenu}>
          <div className="logo-container">
            <img
              src="/images/logo.png"
              alt="Bar do Amorim"
              className="logo-image"
            />
            <div className="logo-text">
              <h1>Bar do Amorim</h1>
              <span>O melhor da regi√£o desde 2019</span>
            </div>
          </div>
        </Link>

        {/* Bot√£o hamburguer (mobile) */}
        <button
          className="mobile-menu-btn"
          onClick={toggleMenu}
          aria-label="Abrir menu"
        >
          ‚ò∞
        </button>

        {/* Navega√ß√£o */}
        <nav className={`nav-center ${isMenuOpen ? 'active' : ''}`}>
          <Link
            to="/"
            className={location.pathname === '/' ? 'active' : ''}
            onClick={closeMenu}
          >
            üè† In√≠cio
          </Link>
          <Link
            to="/cardapio"
            className={location.pathname === '/cardapio' ? 'active' : ''}
            onClick={closeMenu}
          >
            üìã Card√°pio
          </Link>
          <Link
            to="/ambiente"
            className={location.pathname === '/ambiente' ? 'active' : ''}
            onClick={closeMenu}
          >
            üì∏ Ambiente
          </Link>
          <Link
            to="/sobre"
            className={location.pathname === '/sobre' ? 'active' : ''}
            onClick={closeMenu}
          >
            ‚ÑπÔ∏è Sobre
          </Link>
          <Link
            to="/avalie"
            className={location.pathname === '/avalie' ? 'active' : ''}
            onClick={closeMenu}
          >
            ‚≠ê Avalie
          </Link>
        </nav>

        {/* Info (desktop) */}
        <div className="header-info">
          <div className="info-item">üìå N√£o cobramos taxa de 10%</div>
          <div className="info-item">üé∂ Couvert Art√≠stico R$ 7,00</div>
          <div className="info-item">üõú Wi-Fi: bardoamorim</div>
        </div>

        {/* Info (mobile compacta) */}
        <div className="mobile-info">
          <div className="mobile-info-items">
            {/* Linha de cima: taxa + couvert */}
            <div className="mobile-info-row-top">
              <span className="mobile-info-iconline no-tax">
                <span role="img" aria-label="fixo">üìå</span>
                N√£o cobramos taxa de 10%
              </span>

              <span className="mobile-info-iconline cover-charge">
                <span role="img" aria-label="musica">üé∂</span>
                Couvert Art√≠stico R$ 7,00
              </span>
            </div>

            {/* Linha de baixo: wifi */}
            <div className="mobile-info-row-bottom">
              <span className="mobile-info-iconline">
                <span role="img" aria-label="wifi">üõú</span>
                Wi-Fi: bardoamorim
              </span>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
