import React from 'react';
import { Link } from 'react-router-dom';
import { usePriceFormatter } from '../../hooks/usePriceFormatter';
import './ProductCard.css';

const ProductCard = ({ product, onProductClick }) => {
  const { formatPrice } = usePriceFormatter();

  const handleImageError = (e) => {
    e.target.style.display = 'none';
    const placeholder = e.target.nextSibling;
    if (placeholder) {
      placeholder.style.display = 'flex';
    }
  };

  // Se quiser manter o modal tambÃ©m, use esta versÃ£o:
  const handleCardClick = (e) => {
    // Previne que o Link seja acionado se clicarem no card inteiro
    e.preventDefault();
    if (onProductClick) {
      onProductClick(product);
    }
  };

  return (
    <Link 
      to={`/produto/${product.id}`} 
      className="product-card-link"
      onClick={handleCardClick} // Opcional: se quiser manter o modal
    >
      <div className="product-card">
        <div className="product-image">
          {product.image ? (
            <>
              <img 
                src={product.image} 
                alt={product.name}
                onError={handleImageError}
                className="product-image-img"
              />
              <div className="image-placeholder" style={{display: 'none'}}>
                <span>ðŸ“·</span>
                <p>Imagem nÃ£o disponÃ­vel</p>
              </div>
            </>
          ) : (
            <div className="image-placeholder">
              <span>ðŸ“·</span>
              <p>Sem imagem</p>
            </div>
          )}
        </div>
        
        <div className="product-info">
          <h3 className="product-name">{product.name}</h3>
          {product.description && (
            <p className="product-description">{product.description}</p>
          )}
          
          <div className="product-price">
            {product.details ? (
              <span className="price-from">A partir de R$ {formatPrice(product.price)}</span>
            ) : (
              <span className="price-single">R$ {formatPrice(product.price)}</span>
            )}
          </div>
        </div>
      </div>
    </Link>
  );
};

export default ProductCard;
