import React, { useState } from 'react';
import './Ambiente.css';

const Ambiente = () => {
  const [modalAberta, setModalAberta] = useState(false);
  const [fotoAtual, setFotoAtual] = useState(0);

  const fotosAmbiente = [
    {
      id: 1,
      src: '/images/ambiente/ambiente1.jpg',
      alt: 'Vista interna do Bar do Amorim',
      descricao: 'Nosso ambiente aconchegante'
    },
    {
      id: 2,
      src: '/images/ambiente/ambiente2.jpg',
      alt: 'Área externa do bar',
      descricao: 'Área externa para momentos especiais'
    },
    {
      id: 3,
      src: '/images/ambiente/ambiente3.jpg',
      alt: 'Bar do Amorim à noite',
      descricao: 'Ambiente iluminado para happy hour'
    },
  ];

  const abrirModal = (index) => {
    setFotoAtual(index);
    setModalAberta(true);
    // Previne scroll do body quando modal está aberta
    document.body.style.overflow = 'hidden';
  };

  const fecharModal = () => {
    setModalAberta(false);
    // Restaura scroll do body
    document.body.style.overflow = 'auto';
  };

  const fotoAnterior = () => {
    setFotoAtual((prev) => (prev === 0 ? fotosAmbiente.length - 1 : prev - 1));
  };

  const proximaFoto = () => {
    setFotoAtual((prev) => (prev === fotosAmbiente.length - 1 ? 0 : prev + 1));
  };

  // Fechar modal com ESC
  React.useEffect(() => {
    const handleEsc = (event) => {
      if (event.keyCode === 27) {
        fecharModal();
      }
    };
    
    if (modalAberta) {
      window.addEventListener('keydown', handleEsc);
      return () => {
        window.removeEventListener('keydown', handleEsc);
      };
    }
  }, [modalAberta]);

  return (
    <div className="ambiente-page">
      <div className="container">
        <h1>Nosso Ambiente</h1>
        <p className="page-subtitle">
          Conheça o ambiente aconchegante do Bar do Amorim
        </p>
        
        <div className="galeria-ambiente">
          {fotosAmbiente.map((foto, index) => (
            <div 
              key={foto.id} 
              className="foto-item"
              onClick={() => abrirModal(index)}
            >
              <img 
                src={foto.src} 
                alt={foto.alt}
                className="foto-ambiente"
              />
              <p className="foto-descricao">{foto.descricao}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Modal */}
      {modalAberta && (
        <div className="modal-overlay" onClick={fecharModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="modal-close" onClick={fecharModal}>
              ×
            </button>
            
            <button className="modal-nav modal-prev" onClick={fotoAnterior}>
              ‹
            </button>
            
            <img 
              src={fotosAmbiente[fotoAtual].src}
              alt={fotosAmbiente[fotoAtual].alt}
              className="modal-image"
            />
            
            <button className="modal-nav modal-next" onClick={proximaFoto}>
              ›
            </button>
            
            <p className="modal-caption">
              {fotosAmbiente[fotoAtual].descricao}
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Ambiente;
