// components/ProductCard/ProductCard.js
import React from 'react';
import './ProductCard.css';

const ProductCard = ({ product, onProductClick }) => {
  return (
    <div className="product-card" onClick={() => onProductClick(product)}>
      <div className="product-image">
        {/* Aqui vocÃª pode adicionar a imagem do produto */}
        <div className="image-placeholder">
          Imagem do {product.name}
        </div>
      </div>
      
      <div className="product-info">
        <h3 className="product-name">{product.name}</h3>
        <p className="product-description">{product.description}</p>
        
        <div className="product-price">
          {product.details ? (
            <span className="price-from">A partir de R$ {product.price.toFixed(2)}</span>
          ) : (
            <span className="price-single">R$ {product.price.toFixed(2)}</span>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
