import React, { useState } from 'react';
import ProductCard from '../../components/ProductCard/ProductCard';
import './Menu.css';

const Menu = () => {
  const [activeCategory, setActiveCategory] = useState('todos');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedProduct, setSelectedProduct] = useState(null);

  const products = [
    {
      id: 1,
      name: "Picanha na Chapa",
      description: "Suculenta picanha grelhada na chapa, servida com farofa e vinagrete.",
      price: 45.90,
      category: "happy-hour",
    },
    {
      id: 2,
      name: "Costela no Bafo",
      description: "Costela su√≠na preparada no bafo, macia e saborosa.",
      price: 38.90,
      category: "jantar",
    },
    {
      id: 3,
      name: "Por√ß√£o de Fritas",
      description: "Batata frita crocante, por√ß√£o generosa para compartilhar.",
      price: 22.90,
      category: "porcoes",
    },
    {
      id: 4,
      name: "Cerveja Heineken",
      description: "Cerveja pilsen premium, garrafa 600ml.",
      price: 12.90,
      category: "bebidas",
    },
    {
      id: 5,
      name: "Caipirinha de Lim√£o",
      description: "Tradicional caipirinha brasileira com lim√£o e cacha√ßa.",
      price: 15.90,
      category: "drinks",
    },
    {
      id: 6,
      name: "Fil√© √† Parmegiana",
      description: "Fil√© de frango empanado com molho de tomate e queijo derretido.",
      price: 32.90,
      category: "almoco",
    },
    {
      id: 7,
      name: "Feijoada da Casa",
      description: "Feij√£o preto, carnes su√≠nas, paio, bacon, carne seca e temperos da casa. Acompanha couve refogada, torresmo, pururuca, farofa da casa, vinagrete apimentado e arroz branco.",
      price: 49.00,
      category: "almoco",
      details: {
        individual: "R$ 49,00",
        media: "R$ 98,00", 
        grande: "R$ 180,00",
        observacao: "Somente aos s√°bados"
      }
    }
  ];

  const categories = [
    { id: 'todos', name: 'Todos' },
    { id: 'happy-hour', name: 'Happy Hour' },
    { id: 'almoco', name: 'Almo√ßo' },
    { id: 'jantar', name: 'Jantar' },
    { id: 'espetos', name: 'Espetos' },
    { id: 'porcoes', name: 'Por√ß√µes' },
    { id: 'bebidas', name: 'Bebidas' },
    { id: 'drinks', name: 'Drinks' }
  ];

  const filteredProducts = products.filter(product => {
    const matchesCategory = activeCategory === 'todos' || product.category === activeCategory;
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
                         product.description.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  const handleProductClick = (product) => {
    setSelectedProduct(product);
  };

  const closeModal = () => {
    setSelectedProduct(null);
  };

  return (
    <div className="menu-page">
      <div className="container">
        <h1>Card√°pio</h1>
        
        <div className="menu-controls">
          <div className="search-bar">
            <input
              type="text"
              placeholder="üîçÔ∏é  Buscar produto..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          
          <div className="category-filters">
            {categories.map(category => (
              <button
                key={category.id}
                className={`category-btn ${activeCategory === category.id ? 'active' : ''}`}
                onClick={() => setActiveCategory(category.id)}
              >
                {category.name}
              </button>
            ))}
          </div>
        </div>
        
        <div className="products-grid">
          {filteredProducts.map(product => (
            <ProductCard 
              key={product.id} 
              product={product} 
              onProductClick={handleProductClick}
            />
          ))}
        </div>
        
        {filteredProducts.length === 0 && (
          <div className="no-results">
            <p>Nenhum produto encontrado para sua busca.</p>
          </div>
        )}
      </div>

      {/* Modal de Detalhes do Produto */}
      {selectedProduct && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="modal-close" onClick={closeModal}>√ó</button>
            <h2>{selectedProduct.name}</h2>
            <p className="modal-description">{selectedProduct.description}</p>
            
            {selectedProduct.details ? (
              <div className="price-details">
                <h3>Valores:</h3>
                <div className="price-options">
                  <div className="price-option">
                    <span className="price-label">Individual:</span>
                    <span className="price-value">{selectedProduct.details.individual}</span>
                  </div>
                  <div className="price-option">
                    <span className="price-label">M√©dia:</span>
                    <span className="price-value">{selectedProduct.details.media}</span>
                  </div>
                  <div className="price-option">
                    <span className="price-label">Grande:</span>
                    <span className="price-value">{selectedProduct.details.grande}</span>
                  </div>
                </div>
                <p className="modal-observation">{selectedProduct.details.observacao}</p>
              </div>
            ) : (
              <div className="price-single">
                <span className="modal-price">R$ {selectedProduct.price.toFixed(2)}</span>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Menu;
