import React, { useState, useCallback } from 'react';
import './Ambiente.css';

const Ambiente = () => {
  const [modalAberta, setModalAberta] = useState(false);
  const [fotoAtual, setFotoAtual] = useState(0);

  const fotosAmbiente = [
    {
      id: 1,
      src: '/images/ambiente/ambiente1.jpg',
      alt: 'Vista interna do Bar do Amorim',
      descricao: 'A fachada mais famosa da cidade!'
    },
    {
      id: 2,
      src: '/images/ambiente/ambiente2.jpg',
      alt: 'Área externa do bar',
      descricao: 'Primeiro parklet de Marília é nosso em parceria com Amstel'
    },
    {
      id: 3,
      src: '/images/ambiente/ambiente3.jpg',
      alt: 'Bar do Amorim à noite',
      descricao: 'Ambiente iluminado perfeito para happy hour'
    },
  ];

  const abrirModal = (index) => {
    setFotoAtual(index);
    setModalAberta(true);
    document.body.style.overflow = 'hidden';
  };

  const fecharModal = () => {
    setModalAberta(false);
    document.body.style.overflow = 'auto';
  };

  const fotoAnterior = useCallback(() => {
    setFotoAtual((prev) => (prev === 0 ? fotosAmbiente.length - 1 : prev - 1));
  }, [fotosAmbiente.length]);

  const proximaFoto = useCallback(() => {
    setFotoAtual((prev) => (prev === fotosAmbiente.length - 1 ? 0 : prev + 1));
  }, [fotosAmbiente.length]);

  const irParaFoto = (index) => {
    setFotoAtual(index);
  };

  React.useEffect(() => {
    const handleKeyDown = (event) => {
      if (!modalAberta) return;
      
      switch(event.key) {
        case 'Escape':
          fecharModal();
          break;
        case 'ArrowLeft':
          fotoAnterior();
          break;
        case 'ArrowRight':
          proximaFoto();
          break;
        default:
          break;
      }
    };
    
    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [modalAberta, fotoAnterior, proximaFoto]);

  return (
    <div className="ambiente-page">
      <div className="container">
        <h1>Nosso Ambiente</h1>
        <p className="page-subtitle">
          Conheça o ambiente acolhedor e descontraído do Bar do Amorim
        </p>
        
        <div className="galeria-ambiente">
          {fotosAmbiente.map((foto, index) => (
            <div 
              key={foto.id} 
              className="foto-item"
              onClick={() => abrirModal(index)}
            >
              <div className="foto-overlay">
                <span className="ver-mais-btn">Ver mais</span>
              </div>
              <img 
                src={foto.src} 
                alt={foto.alt}
                className="foto-ambiente"
              />
              <p className="foto-descricao">{foto.descricao}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Modal com Indicadores Fora da Imagem */}
      {modalAberta && (
        <div className="modal-overlay" onClick={fecharModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            {/* Botão Fechar */}
            <button 
              className="modal-close" 
              onClick={fecharModal}
              aria-label="Fechar galeria"
            >
              <svg width="28" height="28" viewBox="0 0 24 24" fill="none">
                <path d="M18 6L6 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
                <path d="M6 6L18 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
              </svg>
            </button>
            
            {/* Setas de Navegação - Fora da Imagem */}
            <button 
              className="modal-nav modal-prev" 
              onClick={fotoAnterior}
              aria-label="Foto anterior"
            >
              <svg width="32" height="32" viewBox="0 0 24 24" fill="none">
                <path d="M15 18L9 12L15 6" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
              </svg>
            </button>
            
            <button 
              className="modal-nav modal-next" 
              onClick={proximaFoto}
              aria-label="Próxima foto"
            >
              <svg width="32" height="32" viewBox="0 0 24 24" fill="none">
                <path d="M9 18L15 12L9 6" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
              </svg>
            </button>
            
            {/* Imagem em Destaque - Maior */}
            <div className="modal-image-container">
              <img 
                src={fotosAmbiente[fotoAtual].src}
                alt={fotosAmbiente[fotoAtual].alt}
                className="modal-image"
              />
            </div>
            
            {/* Indicador de Fotos - Fora da Imagem */}
            <div className="modal-indicator">
              {fotosAmbiente.map((_, index) => (
                <button
                  key={index}
                  className={`indicator-dot ${index === fotoAtual ? 'active' : ''}`}
                  onClick={() => irParaFoto(index)}
                  aria-label={`Ir para foto ${index + 1}`}
                />
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Ambiente;
