import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const location = useLocation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <header className="header">
      <div className="container">
        {/* Logo √† esquerda */}
        <Link to="/" className="logo" onClick={closeMenu}>
          <div className="logo-container">
            <img src="/images/logo.png" alt="Bar do Amorim" className="logo-image" />
            <div className="logo-text">
              <h1>Bar do Amorim</h1>
              <span>O melhor da regi√£o desde 2019</span>
            </div>
          </div>
        </Link>
        
        {/* Bot√£o do menu mobile */}
        <button 
          className="mobile-menu-btn"
          onClick={toggleMenu}
          aria-label="Abrir menu"
        >
          ‚ò∞
        </button>
        
        {/* Navega√ß√£o no centro */}
        <nav className={`nav-center ${isMenuOpen ? 'active' : ''}`}>
          <Link 
            to="/" 
            className={location.pathname === '/' ? 'active' : ''}
            onClick={closeMenu}
          >
            üè† In√≠cio
          </Link>
          <Link 
            to="/cardapio" 
            className={location.pathname === '/cardapio' ? 'active' : ''}
            onClick={closeMenu}
          >
            üìã Card√°pio
          </Link>
          <Link 
            to="/ambiente" 
            className={location.pathname === '/ambiente' ? 'active' : ''}
            onClick={closeMenu}
          >
            üì∏ Ambiente
          </Link>
          <Link 
            to="/sobre" 
            className={location.pathname === '/sobre' ? 'active' : ''}
            onClick={closeMenu}
          >
            ‚ÑπÔ∏è Sobre
          </Link>
          <Link 
            to="/avalie" 
            className={location.pathname === '/avalie' ? 'active' : ''}
            onClick={closeMenu}
          >
            ‚≠ê Avalie
          </Link>
        </nav>
        
        {/* Informa√ß√µes √† direita */}
        <div className="header-info">
          <div className="info-item">
            üìå N√£o cobramos taxa de 10%
          </div>
          <div className="info-item">
            üé∂ Couvert Art√≠stico R$ 7,00
          </div>
          <div className="info-item">
            üõú Wi-Fi: bardoamorim
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
