import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const location = useLocation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Fecha o menu quando a rota muda
  useEffect(() => {
    setIsMenuOpen(false);
  }, [location]);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <header className="header">
      <div className="container">
        {/* Logo / tÃ­tulo */}
        <Link to="/" className="logo" onClick={closeMenu}>
          <div className="logo-container">
            <img
              src="/images/logo.png"
              alt="Bar do Amorim"
              className="logo-image"
            />
            <div className="logo-text">
              <h1>Bar do Amorim</h1>
              <span>O melhor da regiÃ£o desde 2019</span>
            </div>
          </div>
        </Link>

        {/* BotÃ£o hamburguer (mobile) */}
        <button
          className="mobile-menu-btn"
          onClick={toggleMenu}
          aria-label="Abrir menu"
        >
          â˜°
        </button>

        {/* NavegaÃ§Ã£o */}
        <nav className={`nav-center ${isMenuOpen ? 'active' : ''}`}>
          <Link
            to="/"
            className={location.pathname === '/' ? 'active' : ''}
            onClick={closeMenu}
          >
            âŒ‚ InÃ­cio
          </Link>
          <Link
            to="/cardapio"
            className={location.pathname === '/cardapio' ? 'active' : ''}
            onClick={closeMenu}
          >
            ðŸ“‹ CardÃ¡pio
          </Link>
          <Link
            to="/ambiente"
            className={location.pathname === '/ambiente' ? 'active' : ''}
            onClick={closeMenu}
          >
            ðŸ“¸ Ambiente
          </Link>
          <Link
            to="/sobre"
            className={location.pathname === '/sobre' ? 'active' : ''}
            onClick={closeMenu}
          >
            â„¹ï¸Ž Sobre
          </Link>
          <Link
            to="/avalie"
            className={location.pathname === '/avalie' ? 'active' : ''}
            onClick={closeMenu}
          >
            âœ© Avalie
          </Link>
        </nav>

        {/* InformaÃ§Ãµes desktop */}
        <div className="header-info">
          <div className="info-item">ðŸ“Œ NÃ£o cobramos taxa de 10%</div>
          <div className="info-item">ðŸŽ¶ Couvert ArtÃ­stico R$ 7,00</div>
