import React, { useState } from 'react';
import './WorkWithUs.css';

const WorkWithUs = () => {
  const [formData, setFormData] = useState({
    nome: '',
    idade: '',
    cidade: '',
    bairro: '',
    telefone: '',
    email: '',
    mensagem: '',
    curriculo: null,
    consent: false
  });

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : type === 'file' ? files[0] : value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSend = new FormData();
    Object.keys(formData).forEach(key => {
      formDataToSend.append(key, formData[key]);
    });

    try {
      const response = await fetch('/.netlify/functions/send-job-application', {
        method: 'POST',
        body: formDataToSend,
      });

      if (!response.ok) {
        throw new Error('Erro ao enviar candidatura.');
      }

      alert('Obrigado! Seu cadastro foi enviado com sucesso üçª');
      setFormData({
        nome: '',
        idade: '',
        cidade: '',
        bairro: '',
        telefone: '',
        email: '',
        mensagem: '',
        curriculo: null,
        consent: false
      });
    } catch (error) {
      console.error(error);
      alert('N√£o foi poss√≠vel enviar agora. Tente novamente mais tarde.');
    }
  };

  return (
    <div className="work-page">
      <div className="container">
        <h1>Trabalhe Conosco</h1>
        <p className="intro-text">
          Venha fazer parte da equipe <strong>Bar do Amorim</strong>!  
          Preencha o formul√°rio abaixo e envie seu curr√≠culo para avaliarmos sua candidatura.
        </p>

        <form onSubmit={handleSubmit} className="work-form">
          <div className="form-group">
            <label htmlFor="nome">Nome Completo:</label>
            <input type="text" id="nome" name="nome" value={formData.nome} onChange={handleChange} required />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="idade">Idade:</label>
              <input type="number" id="idade" name="idade" value={formData.idade} onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label htmlFor="telefone">Telefone:</label>
              <input type="tel" id="telefone" name="telefone" value={formData.telefone} onChange={handleChange} required placeholder="(00) 00000-0000" />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="cidade">Cidade:</label>
              <input type="text" id="cidade" name="cidade" value={formData.cidade} onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label htmlFor="bairro">Bairro:</label>
              <input type="text" id="bairro" name="bairro" value={formData.bairro} onChange={handleChange} required />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="email">E-mail:</label>
            <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} required placeholder="seu@email.com" />
          </div>

          <div className="form-group">
            <label htmlFor="mensagem">Fale um pouco sobre voc√™ (opcional):</label>
            <textarea id="mensagem" name="mensagem" rows="5" value={formData.mensagem} onChange={handleChange} placeholder="Conte um pouco sobre sua experi√™ncia ou √°rea de interesse..."></textarea>
          </div>

          <div className="form-group">
            <label htmlFor="curriculo">Anexar Curr√≠culo (PDF ou DOC):</label>
            <input type="file" id="curriculo" name="curriculo" accept=".pdf,.doc,.docx" onChange={handleChange} required />
          </div>

          <div className="form-group consent-group">
            <label className="checkbox-label">
              <input type="checkbox" name="consent" checked={formData.consent} onChange={handleChange} required />
              <span>Concordo em compartilhar meus dados para an√°lise de candidatura</span>
            </label>
          </div>

          <button type="submit" className="btn-submit">Enviar Candidatura</button>
        </form>
      </div>
    </div>
  );
};

export default WorkWithUs;
