  const categories = [
    { id: 'todos', name: 'Todos' },
    { id: 'happy-hour', name: 'Happy Hour' },
    { id: 'almoco', name: 'Almoço' },
    { id: 'jantar', name: 'Jantar' },
    { id: 'espetos', name: 'Espetos' },
    { id: 'porcoes', name: 'Porções' },
    { id: 'cervejas', name: 'Cervejas' },
    { id: 'bebidas', name: 'Bebidas' },
    { id: 'drinks', name: 'Drinks' },
    { id: 'aperitivo', name: 'Aperitivo' }
  ];

  const filteredProducts = products.filter(product => {
    const matchesCategory = activeCategory === 'todos' || product.category === activeCategory;
    const matchesSubcategory = activeSubcategory === 'todos' || product.subcategory === activeSubcategory;
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
                       product.description.toLowerCase().includes(searchTerm.toLowerCase());
    
    if (subcategories[activeCategory]) {
      return matchesCategory && matchesSubcategory && matchesSearch;
    }
    return matchesCategory && matchesSearch;
  });

  const handleProductClick = (product) => {
    setSelectedProduct(product);
  };

  const closeModal = () => {
    setSelectedProduct(null);
  };

  const handleCategoryChange = (categoryId) => {
    setActiveCategory(categoryId);
    setActiveSubcategory('todos');
  };

  return (
    <div className="menu-page">
      <div className="container">
        <h1>Cardápio</h1>
        
        <div className="menu-controls">
          <div className="search-bar">
            <input
              type="text"
              placeholder="🔍︎  Buscar produto..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          
          <div className="category-filters">
            {categories.map(category => (
              <button
                key={category.id}
                className={`category-btn ${activeCategory === category.id ? 'active' : ''}`}
                onClick={() => handleCategoryChange(category.id)}
              >
                {category.name}
              </button>
            ))}
          </div>

          {/* Filtros de Subcategoria */}
          {subcategories[activeCategory] && (
            <div className="subcategory-filters">
              {subcategories[activeCategory].map(subcategory => (
                <button
                  key={subcategory.id}
                  className={`subcategory-btn ${activeSubcategory === subcategory.id ? 'active' : ''}`}
                  onClick={() => setActiveSubcategory(subcategory.id)}
                >
                  {subcategory.name}
                </button>
              ))}
            </div>
          )}
        </div>
        
        <div className="products-grid">
          {filteredProducts.map(product => (
            <ProductCard 
              key={product.id} 
              product={product} 
              onProductClick={handleProductClick}
            />
          ))}
        </div>
        
        {filteredProducts.length === 0 && (
          <div className="no-results">
            <p>Nenhum produto encontrado para sua busca.</p>
          </div>
        )}
      </div>

      {/* Modal de Detalhes do Produto */}
      {selectedProduct && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="modal-close" onClick={closeModal}>×</button>
            <h2>{selectedProduct.name}</h2>
            <p className="modal-description">{selectedProduct.description}</p>
            
            {selectedProduct.details ? (
              <div className="price-details">
                <h3>Valores:</h3>
                <div className="price-options">
                  {selectedProduct.category === 'porcoes' ? (
                    <>
                      {selectedProduct.details.individual !== "Não disponível" && (
                        <div className="price-option">
                          <span className="price-label">Meia porção:</span>
                          <span className="price-value">{selectedProduct.details.individual}</span>
                        </div>
                      )}
                      {selectedProduct.details.media !== "Não disponível" && (
                        <div className="price-option">
                          <span className="price-label">Inteira:</span>
                          <span className="price-value">{selectedProduct.details.media}</span>
                        </div>
                      )}
                      {selectedProduct.details.grande !== "Não disponível" && (
                        <div className="price-option">
                          <span className="price-label">Família:</span>
                          <span className="price-value">{selectedProduct.details.grande}</span>
                        </div>
                      )}
                    </>
                  ) : (
                    <>
                      {selectedProduct.details.individual !== "Não disponível" && (
                        <div className="price-option">
                          <span className="price-label">Executivo (1 pessoa):</span>
                          <span className="price-value">{selectedProduct.details.individual}</span>
                        </div>
                      )}
                      {selectedProduct.details.media !== "Não disponível" && (
                        <div className="price-option">
                          <span className="price-label">Comercial (2 pessoas):</span>
                          <span className="price-value">{selectedProduct.details.media}</span>
                        </div>
                      )}
                      {selectedProduct.details.grande !== "Não disponível" && (
                        <div className="price-option">
                          <span className="price-label">À La Carte (até 4 pessoas):</span>
                          <span className="price-value">{selectedProduct.details.grande}</span>
                        </div>
                      )}
                    </>
                  )}
                </div>
                {selectedProduct.details.observacao && (
                  <p className="modal-observation">{selectedProduct.details.observacao}</p>
                )}
              </div>
            ) : (
              <div className="price-single">
                <span className="modal-price">R$ {selectedProduct.price.toFixed(2)}</span>
                {selectedProduct.observacao && (
                  <p className="modal-observation">{selectedProduct.observacao}</p>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

