import React, { useState, useEffect } from 'react';
import './Review.css';

const Review = () => {
  const [formData, setFormData] = useState({
    nome: '',
    email: '',
    telefone: '',
    nota: 0,
    comentario: '',
    consentimento: false
  });
  const [hoverRating, setHoverRating] = useState(0);
  const [metodo_contato, setMetodoContato] = useState('email');
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  const loadReviews = async () => {
    try {
      const response = await fetch('/processa_avaliacao.php?action=get');
      const result = await response.json();
      if (response.ok && result.success) {
        setReviews(result.reviews || []);
      }
    } catch (error) {
      console.error('Erro ao carregar avaliações:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadReviews();
  }, []);

  const formatPhoneNumber = (value) => {
    const numbers = value.replace(/\D/g, '');
    const limitedNumbers = numbers.slice(0, 11);
    if (limitedNumbers.length <= 2) {
      return limitedNumbers;
    } else if (limitedNumbers.length <= 6) {
      return `(${limitedNumbers.slice(0, 2)}) ${limitedNumbers.slice(2)}`;
    } else if (limitedNumbers.length <= 10) {
      return `(${limitedNumbers.slice(0, 2)}) ${limitedNumbers.slice(2, 6)}-${limitedNumbers.slice(6)}`;
    } else {
      return `(${limitedNumbers.slice(0, 2)}) ${limitedNumbers.slice(2, 7)}-${limitedNumbers.slice(7)}`;
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    let newValue = value;

    if (name === 'telefone') {
      newValue = formatPhoneNumber(value);
    }

    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : newValue
    });
  };

  const handleRatingClick = (stars) => {
    setFormData({
      ...formData,
      nota: stars
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      nome: formData.nome,
      email: formData.email,
      telefone: formData.telefone,
      nota: formData.nota,
      comentario: formData.comentario,
      metodo_contato: metodo_contato,
      consentimento: formData.consentimento
    };

    try {
      const response = await fetch('/processa_avaliacao.php', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      const result = await response.json();

      if (!result.success) {
        throw new Error(result.message || 'Erro ao enviar avaliação');
      }

      alert('Obrigado pela sua avaliação!');

      setFormData({
        nome: '',
        email: '',
        telefone: '',
        nota: 0,
        comentario: '',
        consentimento: false
      });
      setHoverRating(0);
      setMetodoContato('email');

      await loadReviews();

    } catch (error) {
      console.error(error);
      alert(error.message || 'Não foi possível enviar agora. Tente novamente.');
    }
  };

  const StarRating = () => (
    <div className="star-rating">
      {[1, 2, 3, 4, 5].map(star => (
        <button
          key={star}
          type="button"
          className={`star ${star <= (hoverRating || formData.nota) ? 'filled' : ''}`}
          onClick={() => handleRatingClick(star)}
          onMouseEnter={() => setHoverRating(star)}
          onMouseLeave={() => setHoverRating(0)}
          aria-label={`Avaliar com ${star} estrela${star > 1 ? 's' : ''}`}
        >
          ★
        </button>
      ))}
      <span className="rating-text">
        {formData.nota > 0 ? `${formData.nota} estrela${formData.nota > 1 ? 's' : ''}` : 'Clique para avaliar'}
      </span>
    </div>
  );

  const renderStars = (rating) => {
    return '★'.repeat(rating) + '☆'.repeat(5 - rating);
  };

  return (
    <div className="review-page">
      <div className="container">
        <h1>Avalie sua Experiência</h1>

        <div className="review-content">
          <div className="review-form">
            <form onSubmit={handleSubmit}>
              <div className="form-content">
                <div className="form-group">
                  <label htmlFor="nome">Nome e Sobrenome *</label>
                  <input
                    type="text"
                    id="nome"
                    name="nome"
                    value={formData.nome}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Como prefere ser contatado? *</label>
                  <div className="contact-method">
                    <div className="radio-option">
                      <input
                        type="radio"
                        id="email-contact"
                        name="contactMethod"
                        value="email"
                        checked={metodo_contato === 'email'}
                        onChange={(e) => setMetodoContato(e.target.value)}
                        required
                      />
                      <label htmlFor="email-contact" className="radio-label">
                        <span className="icon-text">
                          <img src="/images/icons/email.png" alt="Email" className="contact-icon" />
                          Email
                        </span>
                      </label>
                    </div>
                    <div className="radio-option">
                      <input
                        type="radio"
                        id="phone-contact"
                        name="contactMethod"
                        value="phone"
                        checked={metodo_contato === 'phone'}
                        onChange={(e) => setMetodoContato(e.target.value)}
                        required
                      />
                      <label htmlFor="phone-contact" className="radio-label">
                        <span className="icon-text">
                          <img src="/images/icons/telefone.png" alt="Telefone" className="contact-icon" />
                          Telefone
                        </span>
                      </label>
                    </div>
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor={metodo_contato}>
                    {metodo_contato === 'email' ? 'Email *' : 'Telefone *'}
                  </label>
                  <input
                    type={metodo_contato === 'email' ? 'email' : 'tel'}
                    id={metodo_contato}
                    name={metodo_contato}
                    value={formData[metodo_contato]}
                    onChange={handleChange}
                    required
                    placeholder={metodo_contato === 'email' ? 'seu@email.com' : '(00) 00000-0000'}
                    maxLength={metodo_contato === 'phone' ? '15' : undefined}
                  />
                </div>

                <div className="form-group">
                  <label>Avaliação *</label>
                  <StarRating />
                </div>

                <div className="form-group">
                  <label htmlFor="comentario">Comentário</label>
                  <textarea
                    id="comentario"
                    name="comentario"
                    value={formData.comentario}
                    onChange={handleChange}
                    rows="5"
                    placeholder="Conte-nos sobre sua experiência..."
                  ></textarea>
                </div>

                <div className="form-group consent-group">
                  <label className="checkbox-label">
                    <input
                      type="checkbox"
                      name="consentimento"
                      checked={formData.consentimento}
                      onChange={handleChange}
                    />
                    <span>Concordo em compartilhar minha avaliação publicamente</span>
                  </label>
                </div>
              </div>

              <div className="form-footer">
                <button
                  type="submit"
                  className="btn-submit"
                  disabled={formData.nota === 0}
                >
                  Enviar Avaliação
                </button>
              </div>
            </form>
          </div>

          <div className="reviews-list">
            <h2>Avaliações Recentes</h2>

            <div className="reviews-content">
              {loading ? (
                <p className="loading-text">Carregando avaliações...</p>
              ) : reviews.length === 0 ? (
                <p className="no-reviews">Seja o primeiro a avaliar!</p>
              ) : (
                reviews.map(review => (
                  <div key={review.id} className="review-item">
                    <div className="review-header">
                      <h3>{review.nome}</h3>
                      <div className="rating">
                        {renderStars(review.nota)}
                      </div>
                    </div>
                    {review.comentario && (
                      <p className="review-comment">"{review.comentario}"</p>
                    )}
                    <small className="review-date">
                      <span>
                        {new Date(review.data_avaliacao).toLocaleDateString('pt-BR')}
                      </span>
                      <span>•</span>
                      <span>
                        {new Date(review.data_avaliacao).toLocaleTimeString('pt-BR', {
                          hour: '2-digit',
                          minute: '2-digit'
                        })}
                      </span>
                    </small>
                  </div>
                ))
              )}
            </div>
          </div>

        </div>
      </div>
    </div>
  );
};

export default Review;
