import React, { useState } from 'react';
import ProductCard from '../../components/ProductCard/ProductCard';
import './Menu.css';

const Menu = () => {
  const [activeCategory, setActiveCategory] = useState('todos');
  const [searchTerm, setSearchTerm] = useState('');

  const products = [
    {
      id: 1,
      name: "Picanha na Chapa",
      description: "Suculenta picanha grelhada na chapa, servida com farofa e vinagrete.",
      price: 45.90,
      category: "carnes",
    },
    {
      id: 2,
      name: "Costela no Bafo",
      description: "Costela suína preparada no bafo, macia e saborosa.",
      price: 38.90,
      category: "carnes",
    },
    {
      id: 3,
      name: "Porção de Fritas",
      description: "Batata frita crocante, porção generosa para compartilhar.",
      price: 22.90,
      category: "porcoes",
    },
    {
      id: 4,
      name: "Cerveja Heineken",
      description: "Cerveja pilsen premium, garrafa 600ml.",
      price: 12.90,
      category: "bebidas",
    },
    {
      id: 5,
      name: "Caipirinha de Limão",
      description: "Tradicional caipirinha brasileira com limão e cachaça.",
      price: 15.90,
      category: "bebidas",
    },
    {
      id: 6,
      name: "Filé à Parmegiana",
      description: "Filé de frango empanado com molho de tomate e queijo derretido.",
      price: 32.90,
      category: "pratos",
    }
  ];

  const categories = [
    { id: 'todos', name: 'Todos' },
    { id: 'carnes', name: 'Carnes' },
    { id: 'pratos', name: 'Pratos Principais' },
    { id: 'porcoes', name: 'Porções' },
    { id: 'bebidas', name: 'Bebidas' }
  ];

  const filteredProducts = products.filter(product => {
    const matchesCategory = activeCategory === 'todos' || product.category === activeCategory;
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
                         product.description.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  const addToCart = (product) => {
    alert(`${product.name} adicionado ao carrinho!`);
  };

  return (
    <div className="menu-page">
      <div className="container">
        <h1>Cardápio</h1>
        
        <div className="menu-controls">
          <div className="search-bar">
            <input
              type="text"
              placeholder="Buscar produto..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          
          <div className="category-filters">
            {categories.map(category => (
              <button
                key={category.id}
                className={`category-btn ${activeCategory === category.id ? 'active' : ''}`}
                onClick={() => setActiveCategory(category.id)}
              >
                {category.name}
              </button>
            ))}
          </div>
        </div>
        
        <div className="products-grid">
          {filteredProducts.map(product => (
            <ProductCard 
              key={product.id} 
              product={product} 
              onAddToCart={addToCart}
            />
          ))}
        </div>
        
        {filteredProducts.length === 0 && (
          <div className="no-results">
            <p>Nenhum produto encontrado para sua busca.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Menu;
